import { toast } from "react-hot-toast";
import { useSelector } from "react-redux";


const Product = ({post}) => {

  const {cart} = useSelector( (state) => state);
  return (
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <button>
        {
            cart.some( (p) => p.id === post.id) ? 
            (
              <button onClick={ () => toast.remove("Item removed")}>
                Remove Item
              </button>
            ) :
            (
              <button >
                Add Item
              </button>
            )
        }
      </button>
    </div>
  );
};

export default Product;
