      </div>
      <div>
        <p>{item.title}</p>
        <p>{item.description}</p>
        <div>
          <p>{item.price}</p>
          <button onClick={removeFromCart}>
            <FcDeleteDatabase/>
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
