    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <button>
        {
            cart.some( (p) => p.id === post.id) ? 
            (
              <button onClick={removeFromCart}>
                Remove Item
              </button>
            ) :
            (
              <button onClick={addToCart}>
                Add Item
              </button>
            )
        }
      </button>
    </div>
  );
};

export default Product;
